testcase.Script:
	this.log(" Switch to brainpoolP256r1withSHA256");
	this.t.call("cvca?op=change&keyspec=brainpoolP256r1withSHA256");

	this.log(" Generate root certificate");
	this.t.call("cvca?op=linkdp", "Operation completed");

	print(" DVCA synchronous GetCACertificates");
	this.t.call("dvca?op=update", "ok_cert_available");

	this.log(" DVCA synchronous request for domestic initial certificate");
	this.t.call("dvca?op=initial&cvca=UTCVCA", "failure_synchronous_processing_not_possible");

	this.log(" DVCA asynchronous request for domestic initial certificate");
	this.t.call("dvca?op=initialasync&cvca=UTCVCA", "ok_syntax");
	this.t.call("cvca/getcert?index=-1&action=ok_cert_available", "ok_received_correctly");

	print(" TCC synchronous GetCACertificates");
	this.t.call("tcc?op=update", "ok_cert_available");

	this.log(" TCC synchronous request for domestic initial certificate");
	this.t.call("tcc?op=initial", "failure_synchronous_processing_not_possible");

	this.log(" TCC asynchronous request for domestic initial certificate");
	this.t.call("tcc?op=initialasync", "ok_syntax");
	this.t.call("dvca/getcert?index=-1&action=ok_cert_available", "ok_received_correctly");

	print(" VT synchronous GetCACertificates");
	this.t.call("vt?op=update", "ok_cert_available");

	this.log(" VT synchronous request for domestic initial certificate");
	this.t.call("vt?op=initial&holderID=UTVT1234567", "ok_cert_available");

	this.log(" VT synchronous request for domestic renewed certificate");
	this.t.call("vt?op=renew&holderID=UTVT1234567", "ok_cert_available");
