testcase.Script:
	this.log(" Switch to brainpoolP256r1withSHA256");
	this.t.call("cvca?op=change&keyspec=brainpoolP256r1withSHA256");

	this.log(" Generate root certificate");
	this.t.call("cvca?op=linkdp", "Operation completed");

	this.log(" Switch to brainpoolP256r1withSHA256");
	this.t.call("cvca-fu?op=change&keyspec=brainpoolP256r1withSHA256");

	this.log(" Generate root certificate");
	this.t.call("cvca-fu?op=linkdp", "Operation completed");

	this.log(" SPOC asynchronous GetCACertificates");
	this.t.call("cvca?op=getcacertificates", "ok_reception_ack");
	this.t.call("cvca-fu/getcert?index=-1&action=ok_cert_available", "ok_received_correctly");

	this.log(" SPOC synchronous GetCACertificates");
	this.t.call("cvca-fu?op=getcacertificates", "ok_cert_available");

	this.log(" DVCA synchronous GetCACertificates");
	this.t.call("dvca?op=update", "ok_cert_available");

	this.log(" DVCA synchronous request for foreign initial certificate");
	this.t.call("dvca?op=initial&cvca=FUCVCA", "failure_synchronous_processing_not_possible");

	this.log(" DVCA asynchronous request for foreign initial certificate");
	this.t.call("dvca?op=initialasync&cvca=FUCVCA", "ok_syntax");
	this.t.call("cvca/request?index=-1&action=ok_request_forwarded", "ok_reception_ack");
	this.t.call("cvca-fu/request?index=-1&action=ok_cert_available", "ok_received_correctly");

	this.log(" DVCA synchronous request for foreign certificate renewal");
	this.t.call("dvca?op=renew&cvca=FUCVCA", "ok_cert_available");
